{ 8048355: 55 push %ebp 8048356: 89 e5 mov %esp,%ebp 8048358: 53 push %ebx 8048359: 83 ec 04 sub $0x4,%esp 804835c: 8b 5d 08 mov 0x8(%ebp),%ebx return n == 0 ? a[0] : MAX(a[n], max(n-1)); 804835f: 85 db test %ebx,%ebx 8048361: 75 07 jne 804836a <max+0x15> 8048363: a1 a0 95 04 08 mov 0x80495a0,%eax 8048368: eb 18 jmp 8048382 <max+0x2d> 804836a: 8d 43 ff lea -0x1(%ebx),%eax 804836d: 89 04 24 mov %eax,(%esp) 8048370: e8 e0 ff ff ff call 8048355 <max>
